mapper.insert:
insert into ask_answer (id, `time`, title,
  `type`, author, remarks,
  reply_nums, `status`, `desc`
  )
values (#{id,jdbcType=VARCHAR}, #{time,jdbcType=TIMESTAMP}, #{title,jdbcType=VARCHAR},
  #{type,jdbcType=VARCHAR}, #{author,jdbcType=VARCHAR}, #{remarks,jdbcType=VARCHAR},
  #{replyNums,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, #{desc,jdbcType=LONGVARCHAR}
  )

mapper.select:
SELECT FROM ask_answer
AND `type` = #{type,jdbcType=VARCHAR}
AND (title LIKE CONCAT('%',#{content,jdbcType=VARCHAR},'%') OR author LIKE CONCAT('%',#{content,jdbcType=VARCHAR},'%') OR `desc` LIKE CONCAT('%',#{content,jdbcType=VARCHAR},'%'))
AND `status` = 0
ORDER BY `time` DESC LIMIT #{currentPage},#{showRowsPerPage}

SELECT 
FROM ask_answer


    AND `type` = #{type,jdbcType=VARCHAR}


    AND `title` = #{title,jdbcType=VARCHAR}


    AND `author` = #{author,jdbcType=VARCHAR}

  AND `status` = 0
